import React, { useEffect, useRef, useState } from "react";
import { motion } from "framer-motion";

export default function SkillsSection() {
  const [inView, setInView] = useState(false);
  const ref = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) setInView(true);
      },
      { threshold: 0.2 }
    );
    if (ref.current) observer.observe(ref.current);
    return () => {
      if (ref.current) observer.unobserve(ref.current);
    };
  }, []);

  const mainSkills = [
    { name: "Python", logo: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/python/python-original.svg" },
    { name: "R", logo: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/r/r-original.svg" },
    { name: "SQL", logo: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/mysql/mysql-original.svg" },
    { name: "Excel", logo: "https://img.icons8.com/color/480/microsoft-excel-2019--v1.png" },
    { name: "Power BI", logo: "https://img.icons8.com/color/480/power-bi.png" },
    { name: "OpenAI", logo: "https://asset.brandfetch.io/idZKbd65pT/idm22Q8C09.svg" },
    { name: "Microsoft Copilot", logo: "https://img.icons8.com/color/480/microsoft.png" },
    { name: "N8N", logo: "data:image/jpeg;base64," },
    { name: "Power Automate", logo: "https://img.icons8.com/color/480/power-automate.png" },
    { name: "HubSpot", logo: "https://www.vectorlogo.zone/logos/hubspot/hubspot-icon.svg" },
    { name: "Sales Navigator", logo: "https://img.icons8.com/fluency/480/linkedin.png" },
    { name: "Apollo", logo: "https://asset.brandfetch.io/idZuMkHGBl/idp6CKC2B0.svg" },
    { name: "Datagma", logo: "https://cdn.prod.website-files.com/64a451e94283e66242793f67/65feee57e874940f0248f386_Datagma-logo.png" },
    { name: "Microsoft Fabric", logo: "https://static.wikia.nocookie.net/logopedia/images/a/aa/Microsoft_Fabric_2023.svg" },
  ];

  const secondarySkills = [
    { name: "Pandas", logo: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/pandas/pandas-original.svg" },
    { name: "Matplotlib", logo: "https://upload.wikimedia.org/wikipedia/commons/8/84/Matplotlib_icon.svg" },
    { name: "Plotly", logo: "https://images.plot.ly/logo/new-branding/plotly-logomark.png" },
    { name: "scikit-learn", logo: "https://upload.wikimedia.org/wikipedia/commons/0/05/Scikit_learn_logo_small.svg" },
    { name: "BeautifulSoup", logo: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/python/python-original.svg" },
    { name: "Requests", logo: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/python/python-original.svg" },
    { name: "Monday.com", logo: "https://dapulse-res.cloudinary.com/image/upload/f_auto,q_auto/remote_mondaycom_static/img/monday-logo-x2.png" },
    { name: "ClickUp", logo: "https://app-cdn.clickup.com/assets/images/brand/clickup-symbol_purple.svg" },
    { name: "KanbanFlow", logo: "https://kanbanflow.com/images/icon.png" },
    { name: "Skrapp", logo: "https://www.skrapp.io/favicon.ico" },
    { name: "Git", logo: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/git/git-original.svg" },
    { name: "GitHub", logo: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/github/github-original.svg" },
    { name: "PostgreSQL", logo: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/postgresql/postgresql-original.svg" },
    { name: "MongoDB", logo: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/mongodb/mongodb-original.svg" },
    { name: "AWS", logo: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/amazonwebservices/amazonwebservices-original-wordmark.svg" },
    { name: "Azure", logo: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/azure/azure-original.svg" },
    { name: "Jupyter", logo: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/jupyter/jupyter-original.svg" },
    { name: "Docker", logo: "https://cdn.jsdelivr.net/gh/devicons/devicon/icons/docker/docker-original.svg" },
  ];

  const duplicatedMain = [...mainSkills, ...mainSkills];
  const duplicatedSecondary = [...secondarySkills, ...secondarySkills];

  const renderSkill = (skill, index, borderColor) => (
    <motion.div
      key={index}
      className="flex-shrink-0 group cursor-pointer"
      whileHover={{ scale: 1.1, y: -5 }}
      transition={{ duration: 0.2 }}
    >
      <div className={`w-32 h-32 bg-white rounded-2xl shadow-lg hover:shadow-2xl transition-all flex flex-col items-center justify-center gap-3 border-2 border-gray-100 hover:border-[${borderColor}] p-4`}>
        <div className="w-14 h-14 flex items-center justify-center">
          <img
            src={skill.logo}
            alt={skill.name}
            className="w-full h-full object-contain transition-transform group-hover:scale-110"
            onError={(e) => {
              e.target.style.display = "none";
              const fallback = document.createElement("div");
              fallback.className =
                "w-12 h-12 bg-gradient-to-br from-blue-100 to-emerald-100 rounded-xl flex items-center justify-center text-2xl font-bold text-[#007BFF]";
              fallback.textContent = skill.name.charAt(0);
              e.target.parentElement.appendChild(fallback);
            }}
          />
        </div>
        <span className="font-semibold text-xs text-[#222222] text-center">
          {skill.name}
        </span>
      </div>
    </motion.div>
  );

  return (
    <section id="skills" className="py-24 bg-[#FAFAF9] overflow-hidden">
      <div className="max-w-7xl mx-auto px-6">
        <motion.div
          ref={ref}
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <h2 className="text-4xl md:text-5xl font-bold text-[#222222] mb-4">
            Stack <span className="gradient-text">Tecnológico</span>
          </h2>
          <div className="w-20 h-1 bg-gradient-to-r from-[#007BFF] to-[#00C896] mx-auto rounded-full mb-6" />
          <p className="text-gray-600 max-w-2xl mx-auto">
            Herramientas y tecnologías que domino para crear soluciones innovadoras
          </p>
        </motion.div>

        {/* Cinta superior */}
        <div className="relative mb-8">
          <div className="absolute left-0 top-0 bottom-0 w-20 bg-gradient-to-r from-[#FAFAF9] to-transparent z-10" />
          <div className="absolute right-0 top-0 bottom-0 w-20 bg-gradient-to-l from-[#FAFAF9] to-transparent z-10" />
          <motion.div
            className="flex gap-6"
            animate={{ x: [0, -2000] }}
            transition={{ x: { duration: 40, repeat: Infinity, ease: "linear" } }}
          >
            {duplicatedMain.map((skill, index) => renderSkill(skill, index, "#007BFF"))}
          </motion.div>
        </div>

        {/* Cinta inferior */}
        <div className="relative">
          <div className="absolute left-0 top-0 bottom-0 w-20 bg-gradient-to-r from-[#FAFAF9] to-transparent z-10" />
          <div className="absolute right-0 top-0 bottom-0 w-20 bg-gradient-to-l from-[#FAFAF9] to-transparent z-10" />
          <motion.div
            className="flex gap-6"
            animate={{ x: [-2000, 0] }}
            transition={{ x: { duration: 40, repeat: Infinity, ease: "linear" } }}
          >
            {duplicatedSecondary.map((skill, index) => renderSkill(skill, index, "#00C896"))}
          </motion.div>
        </div>
      </div>
    </section>
  );
}
